const Card = ({ card, small, faceDown, highlight }) => {
  const width = small ? 56 : 72;
  const height = small ? 84 : 108;

  if (faceDown) {
    return (
      <div style={{
        width,
        height,
        borderRadius: 8,
        background: 'linear-gradient(145deg, #2a2a4a 0%, #1a1a2e 100%)',
        border: '2px solid #c9a227',
        boxShadow: '0 4px 12px rgba(0,0,0,0.5)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
      }}>
        <div style={{
          width: width - 12,
          height: height - 12,
          borderRadius: 4,
          border: '1px solid #3a3a5a',
          background: 'repeating-linear-gradient(45deg, #252545, #252545 4px, #1e1e3e 4px, #1e1e3e 8px)',
        }} />
      </div>
    );
  }

  const data = typeof card === 'string' ? { rank: card.slice(0, -1), suit: card.slice(-1) } : card;
  const isRed = data.suit === '♥' || data.suit === '♦';
  const color = isRed ? '#c41e3a' : '#1a1a2e';

  const cornerFontSize = small ? 12 : 15;
  const centerFontSize = small ? 28 : 36;

  return (
    <div style={{
      width,
      height,
      borderRadius: 8,
      background: 'linear-gradient(180deg, #ffffff 0%, #f5f0e6 100%)',
      boxShadow: highlight 
        ? '0 0 16px rgba(201,162,39,0.8), 0 4px 12px rgba(0,0,0,0.4)'
        : '0 4px 12px rgba(0,0,0,0.4)',
      border: highlight ? '2px solid #c9a227' : '1px solid #d4c5a9',
      position: 'relative',
      fontFamily: "Georgia, serif",
    }}>
      {/* Top left */}
      <div style={{
        position: 'absolute',
        top: 4,
        left: 5,
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        lineHeight: 1.1,
        color,
      }}>
        <span style={{ fontSize: cornerFontSize, fontWeight: 700 }}>{data.rank}</span>
        <span style={{ fontSize: cornerFontSize }}>{data.suit}</span>
      </div>

      {/* Center */}
      <div style={{
        position: 'absolute',
        top: '50%',
        left: '50%',
        transform: 'translate(-50%, -50%)',
        fontSize: centerFontSize,
        color,
        lineHeight: 1,
      }}>
        {data.suit}
      </div>

      {/* Bottom right */}
      <div style={{
        position: 'absolute',
        bottom: 4,
        right: 5,
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        lineHeight: 1.1,
        color,
        transform: 'rotate(180deg)',
      }}>
        <span style={{ fontSize: cornerFontSize, fontWeight: 700 }}>{data.rank}</span>
        <span style={{ fontSize: cornerFontSize }}>{data.suit}</span>
      </div>
    </div>
  );
};

const Button = ({ children, onClick, variant = 'primary', disabled }) => {
  const styles = {
    primary: { 
      background: 'linear-gradient(180deg, #c9a227 0%, #a68523 50%, #8b6914 100%)',
      color: '#1a1a2e',
      border: '2px solid #d4af37',
    },
    secondary: { 
      background: 'linear-gradient(180deg, #2d4a3e 0%, #1e3329 100%)',
      color: '#c9a227',
      border: '2px solid #3d5a4e',
    },
  };

  return (
    <button
      onClick={onClick}
      disabled={disabled}
      style={{
        padding: '12px 24px',
        borderRadius: 8,
        fontFamily: "'Cinzel', Georgia, serif",
        fontSize: 14,
        fontWeight: 700,
        letterSpacing: '0.5px',
        textTransform: 'uppercase',
        cursor: disabled ? 'not-allowed' : 'pointer',
        opacity: disabled ? 0.5 : 1,
        boxShadow: '0 4px 12px rgba(0,0,0,0.3)',
        ...styles[variant],
      }}
    >
      {children}
    </button>
  );
};

